import React, { useState } from "react";
import Navbar from "./Navbar";
import { ref, uploadBytes } from "firebase/storage";
import { auth, storage } from "./firebase";
import "./App.css";

function Addpost() {
    const [typefile,settypefile] = useState();
    const [text,settext] = useState("");
    const handleclick = () => {
    if(typefile== null) return;
    const imageref = ref(storage,`Post/images${auth.user.uid}`)
    uploadBytes(imageref,typefile).then(()=>{settext("image uploaded")})

    }

    return ( <div>
    <Navbar />
    <div className="d-flex align-items-center justify-content-center">
    <div className="fileupload">
        <input className="inputbox"type="file" onChange={(e)=>{settypefile(e.target.files[0])}}/>
        <br></br>
        <button type="submit" className="btn1" onClick={handleclick}>AddPost</button>
    </div>
    {text===null ? text : ""}
    </div>
    </div>);
}

export default Addpost;
