import React, { useContext, useState } from "react";
import Navbar from "./Navbar";
import { ref, uploadBytes } from "firebase/storage";
import { storage } from "./firebase";
import { MdOutlineDriveFileMove } from "react-icons/md";
import "./App.css";
import { Appcontext } from "./Navi";


function Addpost() {
    const [text,settext] = useState("");
    const {userauth} = useContext(Appcontext);

    const [imageUpload, setImageUpload] = useState(null);

    const imagesListRef = ref(storage, "images/");
    const uploadFile = () => {
        if (imageUpload == null) return;
        const imageRef = ref(storage, `Post/${imageUpload.name + userauth.uid + v4()}`);
        uploadBytes(imageRef, imageUpload).then((snapshot) => {
        getDownloadURL(snapshot.ref).then();
        });
    };

    return ( <div>
    <Navbar />
    <div className="d-flex align-items-center justify-content-center">
    <div className="fileupload">
        <input  type="file" id="up" style={{display:"none"}} onChange={(e)=>{setImageUpload(e.target.files[0])}}/>
        <label for="up" style={{cursor:"pointer"}}><MdOutlineDriveFileMove size={100} ></MdOutlineDriveFileMove></label>
        <br></br>
        <button type="submit" className="btn2" onClick={uploadFile}>AddPost</button>
    </div>
    </div>
    <br></br>
    <center className="text1" style={{color:"burlywood",fontFamily:"fantasy",fontSize:"larger"}}>
    <input type="text"/>
    {<center>{text}</center>}</center>

    
    </div>);
}


export default  Addpost;
